import React, { useState, useEffect } from 'react';
import './todo.css'; // Assuming you have a CSS file for styling

const TaskList = () => {
  const [_tasks, _setTasks] = useState([]);
  const [_newTask, _setNewTask] = useState('');

  useEffect(() => {
    const form = document.querySelector("#new-task-form");
    const input = document.querySelector("#new-task-input");
    const list_el = document.querySelector("#tasks");

    form.addEventListener('submit', (e) => {
      e.preventDefault();

      const task = input.value;

      const task_el = document.createElement('div');
      task_el.classList.add('task');

      const task_content_el = document.createElement('div');
      task_content_el.classList.add('content');

      task_el.appendChild(task_content_el);

      const task_input_el = document.createElement('input');
      task_input_el.classList.add('text');
      task_input_el.type = 'text';
      task_input_el.value = task;
      task_input_el.setAttribute('readOnly', 'readOnly');

      task_content_el.appendChild(task_input_el);

      const task_actions_el = document.createElement('div');
      task_actions_el.classList.add('actions');

      const task_edit_el = document.createElement('button');
      task_edit_el.classList.add('edit');
      task_edit_el.innerText = 'Edit';

      const task_delete_el = document.createElement('button');
      task_delete_el.classList.add('delete');
      task_delete_el.innerText = 'Delete';

      task_actions_el.appendChild(task_edit_el);
      task_actions_el.appendChild(task_delete_el);

      task_el.appendChild(task_actions_el);

      list_el.appendChild(task_el);

      input.value = '';

      let isCompleted = false;

      task_edit_el.addEventListener('click', () => {
        if (task_edit_el.innerText.toLowerCase() === 'Edit') {
          task_edit_el.innerText = 'Save';
          task_input_el.removeAttribute('readOnly');
          task_input_el.focus();
        } else {
          task_edit_el.innerText = 'Edit';
          task_input_el.setAttribute('readOnly', 'readOnly');
        }
      });

      task_delete_el.addEventListener('click', () => {
        list_el.removeChild(task_el);
      });

      task_content_el.addEventListener('click', () => {
        if (isCompleted) {
          task_input_el.style.textDecoration = 'none';
        } else {
          task_input_el.style.textDecoration = 'line-through';
        }
        isCompleted = !isCompleted;
      });
    });
  }, []); // Empty dependency array to ensure the effect runs only once on component mount

  return (
    <div>
      <header>
        <h1>Task List</h1>
        <form id="new-task-form">
          <input
            type="text"
            name="new-task-input"
            id="new-task-input"
            placeholder="What do you have planned?"
            value={_newTask}
            onChange={() => {}} // Placeholder onChange function
          />
          <input type="submit" id="new-task-submit" value="Add task" />
        </form>
      </header>
      <main>
        <section className="task-list">
          <h2>Tasks</h2>
          <div id="tasks"></div>
        </section>
      </main>
    </div>
  );
};

export default TaskList;
