import React from 'react';
import './pa.css'; // Import your CSS file

const AffirmationBoxes = () => {
  const affirmations = [
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-1.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-2.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-3.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-4.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-5.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-6.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-7.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-8.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-9.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/aff-10.jpeg"
  ];

  const backgroundImages = [
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg",
    "C:/Users/vismaya/Desktop/PA/ASSETS/dog.jpeg"
  ];

  let openedBox = null;

  return (
    <div className="container" id="affirmationContainer">
      {[...Array(2)].map((_, i) => (
        <div className="row" key={i}>
          {[...Array(i === 0 ? 5 : 4)].map((_, j) => {
            const boxIndex = i * 5 + j;
            return (
              <div className="box" key={j} onClick={() => handleBoxClick(boxIndex)}>
                <img className="background-image" src={backgroundImages[boxIndex]} alt={`Background ${boxIndex}`} />
                <img className="affirmation-image" src={affirmations[boxIndex]} alt={`Affirmation ${boxIndex}`} />
              </div>
            );
          })}
        </div>
      ))}
    </div>
  );

  function handleBoxClick(index) {
    if (openedBox) {
      openedBox.classList.remove("clicked");
    }
    const boxes = document.querySelectorAll('.box');
    boxes[index].classList.add('clicked');
    openedBox = boxes[index];
  }
};

export default AffirmationBoxes;
