import React, { useState } from 'react';
import './todo.css';

function TaskList() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  const handleAddTask = (e) => {
    e.preventDefault();

    const task = { id: Date.now(), text: newTask, completed: false };

    const taskElement = (
      <div className="task" key={task.id}>
        <div className="content">
          <input
            type="text"
            className="text"
            value={task.text}
            readOnly
            style={{ textDecoration: task.completed ? 'line-through' : 'none' }}
            onClick={() => handleToggleComplete(task)}
          />
        </div>
        <div className="actions">
          <button className="edit" onClick={() => handleEditTask(task)}>
            Edit
          </button>
          <button className="delete" onClick={() => handleDeleteTask(task)}>
            Delete
          </button>
        </div>
      </div>
    );

    setTasks((prevTasks) => [...prevTasks, taskElement]);
    setNewTask('');
  };

  const handleEditTask = (editedTask) => {
    setTasks((prevTasks) => {
      return prevTasks.map((taskElement) => {
        if (taskElement.key === editedTask.id) {
          taskElement.props.children[0].props.value = editedTask.text;
        }
        return taskElement;
      });
    });
  };

  const handleDeleteTask = (clickedTask) => {
    setTasks((prevTasks) => prevTasks.filter((taskElement) => taskElement.key !== clickedTask.id));
  };

  const handleToggleComplete = (clickedTask) => {
    setTasks((prevTasks) => {
      return prevTasks.map((taskElement) => {
        if (taskElement.key === clickedTask.id) {
          const newCompleted = !clickedTask.completed;
          taskElement.props.children[0].props.style.textDecoration = newCompleted
            ? 'line-through'
            : 'none';
          clickedTask.completed = newCompleted;
        }
        return taskElement;
      });
    });
  };

  return (
    <body className='todo'>
    <div>
      <header className='todo-header'>
        <h1>Task List</h1>
        <form id="new-task-form" onSubmit={handleAddTask}>
          <input
            type="text"
            name="new-task-input"
            id="new-task-input"
            placeholder="What do you have planned?"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
          />
          <input type="submit" id="new-task-submit" value="Add task" />
        </form>
      </header>
      <main>
        <section className="task-list">
          <h2>Tasks</h2>
          <div id="tasks">
            {tasks.map((taskElement) => taskElement)}
          </div>
        </section>
      </main>
    </div>
    </body>
  );
}

export default TaskList;
