import React from 'react';
import './homepage.css';

const Homepage = ({ setLoginUser }) => {
    function navigateToPage(event) {
        const selectedOption = event.target.value;

        switch (selectedOption) {
            case 'to-do-list':
                window.location.href = 'file:///C:/Users/vismaya/Desktop/TO-DO/to_do.html';
                break;
            case 'journal':
                window.location.href = 'journal.html';
                break;
            case 'pomodoro':
                window.location.href = 'pomodoro.html';
                break;
            case 'meditation':
                window.location.href = 'meditation.html';
                break;
            case 'hydration-tracker':
                window.location.href = 'hydration.html';
                break;
            case 'positive-affirmation':
                window.location.href = 'affirmation.html';
                break;
            default:
                break;
        }
    }

    return (
        <div className="homepage">
            <h1 id="heading">Habitify today for a Better tomorrow</h1>

            <div className="logout-button" onClick={() => setLoginUser({})}>
                Logout
            </div>

            <select id="dropdown" onChange={navigateToPage}>
                <option value="" disabled selected>
                    What's on your mind?
                </option>
                <option value="to-do-list" onClick={navigateToPage}>To-Do List</option>
                <option value="journal">Journal</option>
                <option value="pomodoro">Pomodoro</option>
                <option value="meditation">Meditation</option>
                <option value="hydration-tracker">Hydration Tracker</option>
                <option value="positive-affirmation">Positive Affirmation</option>
            </select>
        </div>
    );
};

export default Homepage;
