import React, { useState } from 'react';
import './todo.css'; // Make sure to import your stylesheet

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  const addTask = (e) => {
    e.preventDefault();

    if (newTask.trim() === '') {
      return;
    }

    setTasks((prevTasks) => [
      ...prevTasks,
      { id: Date.now(), content: newTask, isCompleted: false },
    ]);

    setNewTask('');
  };

  const handleEdit = (taskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId
          ? { ...task, isEditing: !task.isEditing }
          : task
      )
    );
  };

  const handleSave = (taskId, updatedContent) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId ? { ...task, content: updatedContent, isEditing: false } : task
      )
    );
  };

  const handleDelete = (taskId) => {
    setTasks((prevTasks) => prevTasks.filter((task) => task.id !== taskId));
  };

  const handleToggleCompletion = (taskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId
          ? { ...task, isCompleted: !task.isCompleted }
          : task
      )
    );
  };

  return (
    <body className='todo'>
    <div>
      <header className='todo-header'>
        <h1>Task List</h1>
        <form onSubmit={addTask}>
          <input
            type="text"
            name="new-task-input"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            placeholder="What do you have planned?"
          />
          <input type="submit" value="Add task" />
        </form>
      </header>
      <main>
        <section className="task-list">
          <h2>Tasks</h2>
          <div id="tasks">
            {tasks.map((task) => (
              <div key={task.id} className="task">
                <div className="content">
                  {task.isEditing ? (
                    <input
                      type="text"
                      className="text"
                      value={task.content}
                      onChange={(e) => handleSave(task.id, e.target.value)}
                    />
                  ) : (
                    <input
                      type="text"
                      className="text"
                      value={task.content}
                      readOnly
                      style={{
                        textDecoration: task.isCompleted ? 'line-through' : 'none',
                      }}
                    />
                  )}
                </div>
                <div className="actions">
                  <button
                    className="edit"
                    onClick={() => handleEdit(task.id)}
                  >
                    {task.isEditing ? 'Save' : 'Edit'}
                  </button>
                  <button
                    className="delete"
                    onClick={() => handleDelete(task.id)}
                  >
                    Delete
                  </button>
                </div>
                <div
                  className="toggle-completion"
                  onClick={() => handleToggleCompletion(task.id)}
                ></div>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
    </body>
  );
};

export default TaskList;
